package chapter3;import java.util.Scanner;public class Lab1Bai2 {    public static void main(String[] args) {        Scanner input = new Scanner(System.in);        System.out.print("Enter width: ");        float width = input.nextFloat();        System.out.print("Enter length: ");        float length = input.nextFloat();        float perimeter = (width + length) * 2;        float area =  width * length;        float minSide = Math.min(width, length);        System.out.printf("Perimeter = %.3f", perimeter);        System.out.println();        System.out.printf("Area = %.3f", area);        System.out.println();        System.out.printf("Smallest side = %.3f",  minSide);        input.close();    }}