package chapter4;import java.util.Scanner;public class Video29 {    public static void main(String[] args) {        Scanner input = new Scanner(System.in);        System.out.print("Enter avg score: ");        float avgScore = input.nextFloat();        if(avgScore >= 9.0 && avgScore < 10){            System.out.println("You have a excellent score ");        }        else if(avgScore >= 8.0 && avgScore < 9.0) {            System.out.println("You have a very good score");        }        else if(avgScore >= 6.5 && avgScore < 8.0) {            System.out.println("You have a good score");        }        else if(avgScore >= 5.0 && avgScore < 6.5) {            System.out.println("You have a average score");        }        else if(avgScore >= 0 && avgScore < 5.0) {            System.out.println("You have a below average score");        }        else {            System.out.println("Invalid score!");        }        input.close();    }}