package chapter5;import java.util.Scanner;public class Video39 {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        System.out.print("Nhập số lượng phần tử: ");        int n = sc.nextInt();        float[] arr = new float[n];        for(int i = 0; i < n; i++){            System.out.print("Nhập phần tử thứ " + (i + 1) + ": ");            arr[i] = sc.nextFloat();        }        System.out.print("Mảng vừa nhập là: ");        for(int i = 0; i < n; i++){            System.out.print(arr[i] + " ");        }        float sum = 0;        for(int i = 0; i < n; i++){            sum = sum + arr[i];        }        System.out.println();        System.out.print("Tổng các phần tử trong mảng bằng: " + sum);        float max = 0;        for(int i = 0; i < n; i++){            if(max < arr[i]) {                max = arr[i];            }        }        System.out.println();        System.out.print("Số lớn nhất trong mảng: " + max);    }}