package chapter5;public class Video43 {    public static void main(String[] args) {        double[] marks = {8.5, 7.0, 9.0, 6.5, 8.0};        double sum = 0;        for(double item : marks) {            sum += item;        }        double avgMark = sum / marks.length;        System.out.println("Tổng điểm của mảng là: " + sum);        System.out.println("Điểm trung bình của mảng là: " + avgMark);    }}