package chapter5;public class Video44 {    public static void main(String[] args) {        String name = "phamduyanh nha o dau the";        System.out.println("Chiều dài chuỗi: " + name.length());        System.out.println("Viết hoa hết tất cả: " + name.toUpperCase());        System.out.println("Viết thường hết tất cả: " + name.toLowerCase());        System.out.println("Lấy ký tự tại ví trí thứ 3: " + name.charAt(3));        System.out.println("Có bao gồm ký tự phamduyanh không? " + name.contains("phamduyanh"));    }}