package chapter5;import java.util.Scanner;public class Video47 {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        System.out.print("Nhập số lượng học sinh: ");        int n = sc.nextInt();        sc.nextLine();        String[] names = new String[n];        double[] marks = new double[n];        for (int i = 0; i < n; i++) {            System.out.print("Nhập tên học sinh thứ " + (i + 1) + ": ");            names[i] = sc.nextLine();            System.out.print("Nhập điểm của " + names[i] + ": ");            marks[i] = sc.nextDouble();            sc.nextLine();        }        System.out.println("Danh sách học sinh");        for (int i = 0; i < n; i++) {            System.out.println(names[i] + " - " + marks[i]);        }        double sum = 0;        for (int i = 0; i < n; i++) {            sum += marks[i];        }        double avgMark = sum / n;        System.out.println("Điểm trung bình của cả lớp: " + avgMark);        System.out.println("Học sinh có điểm >=8:");        for (int i = 0; i < n; i++) {            if(marks[i] >= 8) {                System.out.println(names[i] + " - " + marks[i]);            }        }        sc.close();    }}