package chapter6.lab04.video60;import chapter6.lab04.model.Employee;public class Video60 {    public static void main(String[] args) {        Employee emp1 = new Employee("Phạm Duy Anh", 25, "IT", 40000);        emp1.setCompany("FPT");        Employee emp2 = new Employee("Nguyễn Linh Chi", 23, "Human Resource", 25000);        emp2.setCompany("Viettel");        System.out.println("===BEFORE RAISE===");        System.out.println();        emp1.showInfo();        System.out.println();        emp2.showInfo();        System.out.println("===AFTER RAISE===");        emp1.raiseSalary(4);        emp1.showInfo();        emp2.raiseSalary(15);        emp2.showInfo();    }}