package chapter8.video80;public class FullTimeEmployee extends Employee {    private double monthSalary;    public FullTimeEmployee(double monthSalary, String name, String department) {        super(name, department);        this.monthSalary = monthSalary;    }    public double setMonthSalary() {        return this.monthSalary;    }    public void getMonthSalary(double monthSalary) {        this.monthSalary = monthSalary;    }    @Override    public double calculateSalary() {        return this.monthSalary;    }    public void printInfo() {        System.out.println("Name: " + super.getName() + " | Department: " + super.getDepartment() + " | Type: Full-time");    }}