package chapter9.lab07;public class Lab07 {    public static void main(String[] args) {        Runnable task = new Runnable() {            public void run() {                OrderStatus myStatus = OrderStatus.SHIPPED;                Order myOrder = new Order("A001", myStatus);                myOrder.processOrder();                Order.CustomerInfo data = myOrder.new CustomerInfo("Phạm Duy Anh", "08658829102");                data.printInfo();                Order.DiscountHelper od = new Order.DiscountHelper();                System.out.println("Chiết khấu áp dụng: " + od.calculateDiscount(myStatus));                System.out.println("Đơn hàng " + myOrder.getId() + " đang được xử lý ở trạng thái: " + myStatus.getDescription());            }        };        task.run();    }}