package chapter9.lab07;public enum OrderStatus {    PENDING("Chờ xác nhận"),    SHIPPED("Đang giao hàng"),    DELIVERED("Đã giao"),    CANCELLED("Đã hủy");    private final String description;    OrderStatus(String description) {        this.description = description;    }    public String getDescription() {        return description;    }    public boolean hasFinal() {        if(this == DELIVERED || this == CANCELLED) {            return true;        }        else {            return false;        }    }}