package chapter9.video90;import java.util.Scanner;public class video90 {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        System.out.println("Nhập vào trạng thái của đơn hàng: ");        String status = sc.nextLine().toUpperCase();        try{            OrderStatus input = OrderStatus.valueOf(status);            System.out.println("Trạng thái: " + input.getStatus());            System.out.println("Trạng thái cuối cùng: " + input.hasFinal());        } catch (Exception e) {            System.out.println("Trạng thái không hợp lệ");        }        System.out.println("Status " + status);    }}